import React from "react";
import Won from './Won';
import Lost from "./Lost";

const Slots = (props) => {
    let x = props.x;
    let y = props.y;
    let z = props.z;
    if( (x===y) && (y===z) && (z===x)){
        return (
            <>
                <div className="slot_inner">
                    <h1>{x}{y}{z}</h1>
                    <h1>You have WON</h1>
                    <hr></hr>
                </div>
            </>
        )
    }
    else if ((x!==y) || (y!==z) || (z!==x)){
        return (
            <>
                <div className="slot_inner">
                    <h1>{x}{y}{z}</h1>
                    <h1>You have LOST</h1>
                    <hr></hr>
                </div>
            </>
        )
    }
}

 export default Slots;
