const AppForm = () =>{

    const [text , updatedtext] = useState('')
    const nameinp = (event)=>{
        const newname  = event.target.value;
        document.getElementsByClassName('inp').value = newname;
    }

    const btnClick = () =>{
        updatedtext(newname);
    }

    return(
        <>
        <div>
            <h1>Hello {text}</h1>
            <input className="inp" placeholder="Enter Your Name" type="text" onChange={nameinp} value={''}></input>
            <button onClick={btnClick}>Submit</button>
        </div>
        </>
    );
}


export default AppForm;
